export default function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [startTime, setStartTime] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file);
    if (startTime) formData.append("start_time", startTime);
    const res = await axios.post("http://localhost:8081/delineation", formData);
    setResult(res.data);
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">ECG Delineation Analyzer</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
        <input
          type="datetime-local"
          value={startTime}
          onChange={(e) => setStartTime(e.target.value)}
          className="block"
        />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
          Upload and Analyze
        </button>
      </form>

      {result && (
        <div className="mt-6">
          <p><strong>Mean Heart Rate:</strong> {result.mean_hr} bpm</p>
          <p><strong>Minimum Heart Rate:</strong> {result.min_hr} bpm at {result.min_time}</p>
          <p><strong>Maximum Heart Rate:</strong> {result.max_hr} bpm at {result.max_time}</p>
        </div>
      )}
    </div>
  );
}
