import { useState } from 'react';

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [recordingDate, setRecordingDate] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('recording_date', recordingDate);

    try {
      const response = await fetch('http://localhost:5000/api/v1/delineation', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();
      if (data.error) {
        throw new Error(data.error);
      }

      // Dispatch results to Results component
      window.dispatchEvent(new CustomEvent('analysisResults', { detail: data }));
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to analyze the file: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="upload-form">
      <div className="form-group">
        <label htmlFor="file">Upload ECG Delineation CSV:</label>
        <input
          type="file"
          id="file"
          accept=".csv"
          onChange={(e) => setFile(e.target.files[0])}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="recordingDate">Recording Date (Optional):</label>
        <input
          type="datetime-local"
          id="recordingDate"
          value={recordingDate}
          onChange={(e) => setRecordingDate(e.target.value)}
        />
      </div>
      <button type="submit" disabled={loading || !file}>
        {loading ? 'Processing...' : 'Analyze'}
      </button>
    </form>
  );
};

export default UploadForm;
