import { useState, useEffect } from 'react';

const Results = () => {
  const [results, setResults] = useState(null);
  const [recordingDateTime, setRecordingDateTime] = useState(null);

  useEffect(() => {
    const handleResults = (event) => {
      setResults(event.results);
      setRecordingDateTime(event.recordingDateTime);
    };

    window.addEventListener('analysisResults', handleResults);
    return () => window.removeEventListener('analysisResults', handleResults);
  }, []);

  if (!results) {
    return null;
  }

  return (
    <div className="results-container">
      <h2>Analysis Results</h2>
      <div className="results-grid">
        <div>
          <h3>Mean Heart Rate</h3>
          <p>{results.mean} bpm</p>
        </div>
        <div>
          <h3>Minimum Heart Rate</h3>
          <p>{results.min.rate} bpm at {
              recordingDateTime === null
                ? `${results.min_time} ms`
                : new Date(results.min_time).toLocaleString()
            }
          </p>
        </div>
        <div>
          <h3>Maximum Heart Rate</h3>
          <p>{results.max.rate} bpm at {results.max.time} ms</p>
        </div>
      </div>
    </div>
  );
};

export default Results;
