import { useState } from 'react';
import axios from "axios";

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [recordingDateTime, setRecordingDateTime] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      if (recordingDateTime) formData.append("record_date_time", recordingDateTime);
      console.log(recordingDateTime)
      const res = await axios.post("http://localhost:8081/api/v1/delineation", formData);

      // Dispatch results to Results component
      window.dispatchEvent(new CustomEvent('analysisResults', { results: res.data }));
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to analyze the file: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="upload-form">
      <div className="form-group">
        <label htmlFor="file">Upload ECG Delineation CSV: </label>
        <input
          type="file"
          id="file"
          accept=".csv"
          onChange={(e) => setFile(e.target.files[0])}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="recordingDate">Recording Date (Optional): </label>
        <input
          type="datetime-local"
          id="recordingDate"
          value={recordingDateTime}
          onChange={(e) => setRecordingDateTime(e.target.value)}
        />
      </div>
      <button type="submit" disabled={loading || !file}>
        {loading ? 'Processing...' : 'Analyze'}
      </button>
    </form>
  );
};

export default UploadForm;
