import React from "react";
import UploadForm from "./components/UploadForm";
import Results from "./components/Results";
import "./App.css";

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>ECG Signal Analysis</h1>
      </header>
      <main>
        <UploadForm />
        <Results />
      </main>
    </div>
  );
}

export default App;

