import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  CardHeader,
  Button,
  Typography,
  CircularProgress,
  Paper,
  Grid,
  useTheme
} from '@mui/material';
import {
  FileUpload as FileUploadIcon,
  AccessTime as TimeIcon
} from '@mui/icons-material';
import MonitorHeartIcon from '@mui/icons-material/MonitorHeart';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import ECGWave from './ECGWave';

const ECGMonitor = () => {
  const [heartRateData, setHeartRateData] = useState(null);
  const [uploading, setUploading] = useState(false);
  const theme = useTheme();

  const handleFileUpload = (file) => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('record_date_time', '');

    setUploading(true);

    fetch('http://localhost:8081/api/v1/delineation', {
      method: 'POST',
      body: formData
    })
    .then(response => response.json())
    .then(data => {
      setHeartRateData({
        mean: data.mean,
        min: data.min_hr,
        max: data.max_hr,
        minTime: data.min_time,
        maxTime: data.max_time
      });
    })
    .catch(error => {
      console.error('Error:', error);
    })
    .finally(() => setUploading(false));
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  return (
    <Box sx={{ 
      p: 3, 
      minHeight: '100vh', 
      bgcolor: theme.palette.background.default
    }}>
      <Card sx={{ 
        p: 3, 
        mx: 'auto', 
        maxWidth: 800, 
        boxShadow: 2,
        borderRadius: 2
      }}>
        <CardHeader
          avatar={
            <Box sx={{ 
              bgcolor: theme.palette.primary.main, 
              color: 'white', 
              borderRadius: '50%', 
              p: 1
            }}>
              <MonitorHeartIcon />
            </Box>
          }
          title={
            <Typography variant="h4" component="h1" gutterBottom>
              ECG Heart Rate Monitor
            </Typography>
          }
        />

        <CardContent>
          <Box sx={{ mb: 4 }}>
            <ECGWave />
          </Box>

          <Box sx={{ mb: 4 }}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={12} sm={6}>
                  <DateTimePicker
                    label="Recording Date & Time"
                    value={recordDateTime}
                    onChange={(newValue) => setRecordDateTime(newValue)}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        fullWidth
                        InputProps={{
                          ...params.InputProps,
                          startAdornment: (
                            <CalendarIcon sx={{ mr: 1 }} />
                          ),
                        }}
                      />
                    )}
                    ampm={timeFormat === '12h'}
                    mask="__/__/__ __:__"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth>
                    <InputLabel>Time Format</InputLabel>
                    <Select
                      value={timeFormat}
                      label="Time Format"
                      onChange={(e) => setTimeFormat(e.target.value)}
                    >
                      <MenuItem value="12h">12-hour Clock</MenuItem>
                      <MenuItem value="24h">24-hour Clock</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
              </Grid>
            </LocalizationProvider>
          </Box>

          <Box sx={{ mb: 4, textAlign: 'center' }}>
            <Button
              variant="contained"
              component="label"
              startIcon={uploading ? <CircularProgress size={20} /> : <FileUploadIcon />}
              sx={{
                bgcolor: theme.palette.primary.main,
                '&:hover': {
                  bgcolor: theme.palette.primary.dark
                }
              }}
            >
              Upload ECG Data
              <input
                type="file"
                hidden
                accept=".csv"
                onChange={handleFileSelect}
                disabled={uploading}
              />
            </Button>
          </Box>

          {heartRateData && (
            <Paper elevation={1} sx={{ p: 3 }}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={4}>
                  <Typography variant="h6" gutterBottom>
                    Mean Heart Rate
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {heartRateData.mean} bpm
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Typography variant="h6" gutterBottom>
                    Minimum Heart Rate
                  </Typography>
                  <Typography variant="h5" color="primary">
                    {heartRateData.min} bpm
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    <TimeIcon fontSize="small" /> {heartRateData.minTime}
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Typography variant="h6" gutterBottom>
                    Maximum Heart Rate
                  </Typography>
                  <Typography variant="h5" color="primary">
                    {heartRateData.max} bpm
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    <TimeIcon fontSize="small" /> {heartRateData.maxTime}
                  </Typography>
                </Grid>
              </Grid>
            </Paper>
          )}
        </CardContent>
      </Card>
    </Box>
  );
};

export default ECGMonitor;
