import { useState, useEffect } from 'react';
import { HeartPulse, Clock } from "lucide-react";

const Results = () => {
  const [results, setResults] = useState(null);
  const [recordingDateTime, setRecordingDateTime] = useState('');
  const formatOptions = {
    day: "2-digit",
    month: "2-digit",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
    hour12: false,
  };

  useEffect(() => {
    const handleResults = (event) => {
      setResults(event.detail.data);
      setRecordingDateTime(event.detail.recordingDateTime);
    };

    window.addEventListener('analysisResults', handleResults);
    return () => window.removeEventListener('analysisResults', handleResults);
  }, []);

  if (!results) {
    return null;
  }

  return (
    <div className="results-container">
      <h2>Analysis Results</h2>
      <div className="results-grid">
        <div>
          <h3>Mean Heart Rate</h3>
          <p>{results.mean} bpm</p>
        </div>
        <div>
          <h3>Minimum Heart Rate</h3>
          <p>{results.min_hr} bpm at {" "}
            {
              recordingDateTime === ''
                ? `${results.min_time} ms`
                : new Date(results.min_time).toLocaleString("en-GB", formatOptions)
            }
          </p>
        </div>
        <div>
          <h3>Maximum Heart Rate</h3>
          <p>{results.max_hr} bpm at {" "}
            {
              recordingDateTime === ''
                ? `${results.max_time} ms`
                : new Date(results.max_time).toLocaleString("en-GB", formatOptions)
            }
          </p>
        </div>
      </div>
    </div>
  );
};

export default Results;
